RULES_ITEM.SQL_TEXT:
declare 
    v_count number;
    v_date_constructed_field_id number;
    v_msg varchar2(500);
    v_integration_name integration.integration_name%type;
    
    c_interval number := 7;
    c_date_constructed_field_name varchar2(50) := 'RA_DATE_CONSTRUCTED';
begin
    select config_field_id
      into v_date_constructed_field_id
      from config_field 
     where config_field_name = 'RA_DATE_CONSTRUCTED';
    
    select count(*)
      into v_count
      from config_value_date
     where trunc(ts) between current_date - c_interval and current_date
       and config_field_id = v_date_constructed_field_id
       and trunc(value_date) between current_date - c_interval and current_date;
       
    select integration_name
      into v_integration_name
      from integration
     where integration_id = :id_num;
       
    if v_count = 0 then
        pkg_test_integration.test_failed(
            p_integration_name => v_integration_name,
            p_error => 'No new Registration records in the last 7 days',
            p_integration_run_id => :integration_run_id);
    else     
        pkg_test_integration.test_succeed(v_integration_name, :integration_run_id);
    end if;
       
end;
